#!/bin/bash

echo "Youtube Videos Downloader, powered by youtube-dl."
echo "Script made by Ahmad."

if [ $(dpkg-query -W -f='${Status}' ffmpeg 2>/dev/null | grep -c "ok installed") -eq 0 ];
then
  echo "ffmpeg not installed, installing"
  sudo apt-get install ffmpeg -y;
fi

pkgs='youtube-dl'
if ! dpkg -s $pkgs >/dev/null 2>&1; then
  echo "youtube-dl not install, installing"
  sudo curl -L https://yt-dl.org/downloads/latest/youtube-dl -o /usr/local/bin/youtube-dl
  sudo chmod a+rx /usr/local/bin/youtube-dl
fi

