no-overwrites --download-archive /home/amady/log/archive.log   --output "/home/amady/yt/$folder/%(title)s.%(ext)s" --merge-output-format "mkv"  "$url"
}
read -p "Video/Playlist/Channel Link: " url
read -p "Enter the location of the folder to download to: " folder

#checks if input is empty and puts default location as ~/yt/other/
if [[ -z "$folder" ]]; then
    folder=${folder:-other}
    echo "No input detected, defaulting to ~/yt/"$folder
fi

mkdir -p /home/amady/yt/$folder/
